data.value:
SELECT
  (SELECT COUNT() FROM "jobs" WHERE ("id" = $job_id)) AS "job_count",
  (SELECT COUNT() FROM "partitions" WHERE ("job_id" = $job_id) AND ("is_completed" = 0)) AS "partition_count";

CREATE TABLE IF NOT EXISTS "jobs" ( "id" VARCHAR(256) NOT NULL, "created" DATETIME NOT NULL, "updated" DATETIME NOT NULL, "state" INTEGER NOT NULL, "error" TEXT NULL,
CONSTRAINT "pk_jobs" PRIMARY KEY ("id") ) WITHOUT ROWID;
CREATE TABLE IF NOT EXISTS "partitions" ( "id" CHAR(36) NOT NULL, "job_id" VARCHAR(256) NOT NULL, "created" DATETIME NOT NULL, "updated" DATETIME NOT NULL, "owner" VARCHAR(256) NULL, "first" TEXT NOT NULL, "last" TEXT NOT NULL, "is_inclusive" BOOLEAN NOT NULL, "position" TEXT NULL, "processed" BIGINT NOT NULL, "remaining" BIGINT NOT NULL, "is_completed" BOOLEAN NOT NULL, "is_split_requested" BOOLEAN NOT NULL,
CONSTRAINT "pk_partitions" PRIMARY KEY ("id"), CONSTRAINT "fk_partition_to_job" FOREIGN KEY ("job_id") REFERENCES "jobs"("id") ON UPDATE RESTRICT ON DELETE RESTRICT ) WITHOUT ROWID;
CREATE INDEX "ix_partitions_by_state" ON "partitions" ("job_id", "updated", "owner") WHERE ("is_completed" = 0);
